import { useState, useEffect } from 'react';
import type { Kurse, Dozenten, Raeume } from '@/types/app';
import { APP_IDS } from '@/types/app';
import { extractRecordId, createRecordUrl } from '@/services/livingAppsService';
import {
  Dialog, DialogContent, DialogHeader,
  DialogTitle, DialogFooter,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Select, SelectContent, SelectItem,
  SelectTrigger, SelectValue,
} from '@/components/ui/select';

interface KurseDialogProps {
  open: boolean;
  onClose: () => void;
  onSubmit: (fields: Kurse['fields']) => Promise<void>;
  defaultValues?: Kurse['fields'];
  dozentenList: Dozenten[];
  raeumeList: Raeume[];
}

export function KurseDialog({ open, onClose, onSubmit, defaultValues, dozentenList, raeumeList }: KurseDialogProps) {
  const [fields, setFields] = useState<Partial<Kurse['fields']>>({});
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (open) setFields(defaultValues ?? {});
  }, [open, defaultValues]);

  async function handleSubmit(e: React.FormEvent) {
    e.preventDefault();
    setSaving(true);
    try {
      await onSubmit(fields as Kurse['fields']);
    } finally {
      setSaving(false);
    }
  }

  return (
    <Dialog open={open} onOpenChange={v => !v && onClose()}>
      <DialogContent className="max-w-lg">
        <DialogHeader>
          <DialogTitle>{defaultValues ? 'Kurse bearbeiten' : 'Kurse hinzufügen'}</DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="titel">Titel *</Label>
            <Input
              id="titel"
              value={fields.titel ?? ''}
              onChange={e => setFields(f => ({ ...f, titel: e.target.value }))}
              required
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="beschreibung">Beschreibung</Label>
            <Textarea
              id="beschreibung"
              value={fields.beschreibung ?? ''}
              onChange={e => setFields(f => ({ ...f, beschreibung: e.target.value }))}
              rows={3}
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="startdatum">Startdatum *</Label>
            <Input
              id="startdatum"
              type="date"
              value={fields.startdatum ?? ''}
              onChange={e => setFields(f => ({ ...f, startdatum: e.target.value }))}
              required
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="enddatum">Enddatum</Label>
            <Input
              id="enddatum"
              type="date"
              value={fields.enddatum ?? ''}
              onChange={e => setFields(f => ({ ...f, enddatum: e.target.value }))}
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="max_teilnehmer">Max. Teilnehmer</Label>
            <Input
              id="max_teilnehmer"
              type="number"
              value={fields.max_teilnehmer ?? ''}
              onChange={e => setFields(f => ({ ...f, max_teilnehmer: e.target.value ? Number(e.target.value) : undefined }))}
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="preis">Preis (€)</Label>
            <Input
              id="preis"
              type="number"
              value={fields.preis ?? ''}
              onChange={e => setFields(f => ({ ...f, preis: e.target.value ? Number(e.target.value) : undefined }))}
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="dozent">Dozent</Label>
            <Select
              value={extractRecordId(fields.dozent) ?? 'none'}
              onValueChange={v => setFields(f => ({ ...f, dozent: v === 'none' ? undefined : createRecordUrl(APP_IDS.DOZENTEN, v) }))}
            >
              <SelectTrigger id="dozent"><SelectValue placeholder="Auswählen..." /></SelectTrigger>
              <SelectContent>
                <SelectItem value="none">—</SelectItem>
                {dozentenList.map(r => (
                  <SelectItem key={r.record_id} value={r.record_id}>
                    {r.fields.name ?? r.record_id}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <div className="space-y-2">
            <Label htmlFor="raum">Raum</Label>
            <Select
              value={extractRecordId(fields.raum) ?? 'none'}
              onValueChange={v => setFields(f => ({ ...f, raum: v === 'none' ? undefined : createRecordUrl(APP_IDS.RAEUME, v) }))}
            >
              <SelectTrigger id="raum"><SelectValue placeholder="Auswählen..." /></SelectTrigger>
              <SelectContent>
                <SelectItem value="none">—</SelectItem>
                {raeumeList.map(r => (
                  <SelectItem key={r.record_id} value={r.record_id}>
                    {r.fields.raumname ?? r.record_id}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <div className="space-y-2">
            <Label htmlFor="status">Status</Label>
            <Select
              value={fields.status ?? 'none'}
              onValueChange={v => setFields(f => ({ ...f, status: v === 'none' ? undefined : v }))}
            >
              <SelectTrigger id="status"><SelectValue placeholder="Auswählen..." /></SelectTrigger>
              <SelectContent>
                <SelectItem value="none">—</SelectItem>
                <SelectItem value="geplant">Geplant</SelectItem>
                <SelectItem value="aktiv">Aktiv</SelectItem>
                <SelectItem value="abgeschlossen">Abgeschlossen</SelectItem>
                <SelectItem value="abgesagt">Abgesagt</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <DialogFooter>
            <Button type="button" variant="outline" onClick={onClose}>Abbrechen</Button>
            <Button type="submit" disabled={saving}>
              {saving ? 'Speichern...' : defaultValues ? 'Speichern' : 'Erstellen'}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@300;400;500;600;700&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --font-sans: var(--font-space-grotesk);
}

:root {
  --radius: 0.75rem;

  /* Base palette */
  --background: oklch(0.978 0.004 264);
  --foreground: oklch(0.175 0.02 255);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.175 0.02 255);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.175 0.02 255);

  /* Primary — rich indigo */
  --primary: oklch(0.48 0.195 264);
  --primary-foreground: oklch(0.99 0 0);
  --primary-glow: oklch(0.62 0.18 264);

  --secondary: oklch(0.94 0.01 264);
  --secondary-foreground: oklch(0.3 0.05 264);
  --muted: oklch(0.95 0.005 264);
  --muted-foreground: oklch(0.52 0.02 264);
  --accent: oklch(0.93 0.02 264);
  --accent-foreground: oklch(0.3 0.05 264);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.908 0.012 264);
  --input: oklch(0.908 0.012 264);
  --ring: oklch(0.48 0.195 264);

  /* Charts */
  --chart-1: oklch(0.48 0.195 264);
  --chart-2: oklch(0.62 0.18 264);
  --chart-3: oklch(0.72 0.14 200);
  --chart-4: oklch(0.65 0.18 310);
  --chart-5: oklch(0.72 0.15 55);

  /* Sidebar — deep slate */
  --sidebar: oklch(0.185 0.03 264);
  --sidebar-foreground: oklch(0.92 0.01 264);
  --sidebar-primary: oklch(0.48 0.195 264);
  --sidebar-primary-foreground: oklch(0.99 0 0);
  --sidebar-accent: oklch(0.26 0.035 264);
  --sidebar-accent-foreground: oklch(0.92 0.01 264);
  --sidebar-border: oklch(0.28 0.03 264);
  --sidebar-ring: oklch(0.48 0.195 264);

  /* Custom tokens */
  --font-space-grotesk: 'Space Grotesk', sans-serif;
  --gradient-primary: linear-gradient(135deg, oklch(0.48 0.195 264), oklch(0.62 0.18 264));
  --gradient-hero: linear-gradient(135deg, oklch(0.185 0.03 264) 0%, oklch(0.28 0.05 264) 100%);
  --shadow-elegant: 0 10px 30px -10px oklch(0.48 0.195 264 / 0.3);
  --shadow-card: 0 1px 3px 0 oklch(0.175 0.02 255 / 0.08), 0 1px 2px -1px oklch(0.175 0.02 255 / 0.05);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-space-grotesk);
    font-feature-settings: "ss01", "ss02";
    -webkit-font-smoothing: antialiased;
  }
  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-space-grotesk);
    letter-spacing: -0.02em;
  }
}

@layer utilities {
  .gradient-primary {
    background: var(--gradient-primary);
  }
  .gradient-hero {
    background: var(--gradient-hero);
  }
  .shadow-elegant {
    box-shadow: var(--shadow-elegant);
  }
  .shadow-card {
    box-shadow: var(--shadow-card);
  }
  .transition-smooth {
    transition: var(--transition-smooth);
  }

  /* Hero card gradient variants */
  .stat-card-indigo {
    background: linear-gradient(135deg, oklch(0.48 0.195 264 / 0.08), oklch(0.62 0.18 264 / 0.04));
    border-color: oklch(0.48 0.195 264 / 0.2);
  }
  .stat-card-teal {
    background: linear-gradient(135deg, oklch(0.6 0.14 200 / 0.08), oklch(0.72 0.12 200 / 0.04));
    border-color: oklch(0.6 0.14 200 / 0.2);
  }
  .stat-card-violet {
    background: linear-gradient(135deg, oklch(0.55 0.18 310 / 0.08), oklch(0.65 0.16 310 / 0.04));
    border-color: oklch(0.55 0.18 310 / 0.2);
  }
  .stat-card-amber {
    background: linear-gradient(135deg, oklch(0.72 0.15 55 / 0.08), oklch(0.8 0.12 55 / 0.04));
    border-color: oklch(0.72 0.15 55 / 0.2);
  }
  .stat-card-emerald {
    background: linear-gradient(135deg, oklch(0.62 0.16 160 / 0.08), oklch(0.72 0.14 160 / 0.04));
    border-color: oklch(0.62 0.16 160 / 0.2);
  }

  .icon-indigo { color: oklch(0.48 0.195 264); }
  .icon-teal { color: oklch(0.55 0.14 200); }
  .icon-violet { color: oklch(0.5 0.18 310); }
  .icon-amber { color: oklch(0.65 0.15 55); }
  .icon-emerald { color: oklch(0.52 0.16 160); }

  .badge-geplant { background: oklch(0.9 0.06 264 / 0.5); color: oklch(0.38 0.12 264); }
  .badge-aktiv { background: oklch(0.88 0.1 160 / 0.5); color: oklch(0.38 0.15 160); }
  .badge-abgeschlossen { background: oklch(0.9 0.03 264 / 0.5); color: oklch(0.45 0.03 264); }
  .badge-abgesagt { background: oklch(0.95 0.06 27 / 0.5); color: oklch(0.55 0.2 27); }
}
